vec4 Effect2(vec2 uv) {
    // Create rotating pattern with time-based movement
    vec2 st = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 3.0;

    // Add turbulence to create dynamic noise patterns
    float turb = turbulent(st * 0.5);

    // Calculate color using HSL to RGB conversion with time-based hue
    vec3 color = hsl2rgb(vec3(
        sin(length(st) + iTime) * 0.5 + 0.5, 
        1.0,
        0.6 + turb * 0.4
    ));

    // Add lighting effect using another layer of turbulence
    float light = turbulent(st * 0.2 + vec2(iTime)) * 0.5 + 0.5;

    // Combine color and lighting for final output
    return vec4(color * light, 1.0);
}
