vec4 Effect2(vec2 uv) {
    // Create a transformation matrix for hexagonal tiling
    mat2 m = mat2(0.5, -sqrt(3.0)/2.0, sqrt(3.0)/2.0, 0.5);
    vec2 st = uv * 4.0; // Scale up the UV coordinates

    // Transform to hexagonal grid
    vec2 q = m * (st + iTime); // Add time for animation

    // Calculate distance from center in hexagonal space
    float r = length(q);

    // Create hexagon shape using polar coordinates
    float theta = atan2(q.y, q.x) / 6.0;
    vec2 tc = vec2(r * cos(theta), r * sin(theta));

    // Generate color gradient with time-based rotation
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.5*sin(iTime*0.5 + theta), 1.0, 0.7));
    vec3 mixColor = hsl2rgb(vec3(0.5 + 0.5*cos(iTime*0.5 + theta), 0.8, 0.6));

    // Use noise to add variation between hexagons
    float noiseVal = noise(q * 0.1) * 0.2;
    vec3 finalColor = mix(baseColor, mixColor, noiseVal);

    // Add pulsing effect
    float pulse = sin(iTime*0.5 + r*4.0) * 0.2 + 0.8;
    finalColor *= pulse;

    return vec4(finalColor, 1.0);
}
