vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    float dist = length(uv + 0.5);

    // Create pulsating rings effect using sine wave and noise
    float ringEffect = sin(dist * 10.0 + iTime * 2.0) * 0.5;
    ringEffect += noise(vec3(iTime, uv.x * 4.0, uv.y * 4.0)) * 0.5;

    // Create rotating color wheels using polar coordinates
    vec2 polar = vec2(length(uv), atan2(uv.y, uv.x));
    float angle = polar.y + iTime;

    // Generate multiple rings with different colors
    vec3 color1 = hsv2rgb(vec3(angle * 0.5, 1.0, ringEffect));
    vec3 color2 = hsv2rgb(vec3(angle * 0.7 + 1.0, 1.0, ringEffect + 0.5));

    // Combine effects
    vec3 finalColor = mix(color1, color2, sin(iTime) * 0.5 + 0.5);
    finalColor += noise(vec3(uv * 4.0, iTime)) * 0.3;

    return vec4(finalColor, 1.0);
}
