vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv * 0.5 + 0.5); // Scale UV to 0-1 range

    // Add noise and time-based variation
    float noiseValue = noise(uv * 5.0 + iTime) * 0.5 + 0.5;

    // Create color shifts using HSL
    vec3 baseColor = hsl2rgb(vec3(theta * 180.0 / PI + iTime * 60.0, 0.7, 0.7));
    vec3 noiseColor = hsl2rgb(vec3((r + noiseValue) * 90.0 + iTime * 45.0, 0.6, 0.8));

    // Combine colors with turbulence
    float turb = turbulent(vec2(theta, r)) * 0.5 + 0.5;
    vec3 finalColor = mix(baseColor, noiseColor, turb);

    // Add depth using multiple layers
    finalColor *= (1.0 - abs(r - 0.5) * 2.0) * 0.8 + 0.2;
    finalColor += turbulent(uv * 3.0 + iTime) * 0.4;

    return vec4(finalColor, 1.0);
}
