vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating pattern with time-based rotation
    theta += iTime * 0.5;
    // Create concentric waves using sine function
    float wave1 = sin(r * 8.0 - iTime * 4.0) * 0.5 + 0.5;
    float wave2 = sin(r * 16.0 - iTime * 6.0) * 0.3 + 0.3;
    float wave3 = sin(r * 32.0 - iTime * 8.0) * 0.2 + 0.2;
    // Combine waves and add turbulence
    float pattern = (wave1 + wave2 + wave3) * turbulent(vec2(r, theta)) * 0.5;
    // Create color transitions using HSL to RGB conversion
    vec3 color = hsl(theta * 0.5 + iTime * 0.1, 0.7, pattern * 0.6 + 0.4);
    return vec4(color, 1.0);
}
