vec4 Effect2(vec2 uv) {
    // Animate based on time and position
    float time = iTime * 0.5;

    // Create rotating patterns
    vec2 st = uv * 8.0 + time;
    st += vec2(sin(time + st.x), cos(time + st.y)) * 0.5;

    // Add turbulence for complex patterns
    float t1 = turbulent(st * 0.5);
    float t2 = turbulent(vec3(st, time) * 0.7);

    // Create color transitions using HSL
    vec3 color1 = hsl2rgb(vec3(time + st.x*0.5, 0.6, 0.5));
    vec3 color2 = hsl2rgb(vec3(time + st.y*0.5, 0.7, 0.6));

    // Combine patterns and colors
    float mixAmount = (t1 + t2) * 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    // Add brightness variation
    finalColor *= 0.8 + sin(time + st.x*0.7) * 0.2;

    return vec4(finalColor, 1.0);
}
