vec4 Effect2(vec2 uv) {
    // Calculate distance from center and angle
    float dist = length(uv);
    float angle = atan2(uv.y, uv.x);
    // Create expanding rings using time-based animation
    float ringRadius = dist + sin(iTime * 0.5 + dist * 2.0) * 0.5;
    float ringIndex = mod(ringRadius * 10.0, 3.0); // 3 rings

    // Smooth color transition using HSL
    vec3 color = hsl2rgb(vec3(
        mod(ringIndex + iTime, 3.0) / 3.0, // Hue
        0.7, // Saturation
        0.5 // Lightness
    ));

    // Add noise and turbulence for dynamic effect
    vec2 noiseUV = uv * 10.0;
    float noiseValue = noise(noiseUV);
    color *= 1.0 + noiseValue * 0.3;

    // Create pulsing animation
    float pulse = sin(iTime + dist * 5.0) * 0.2 + 0.8;
    color *= pulse;

    return vec4(color, 1.0);
}
