vec4 Effect2(vec2 uv) {
    // Create base noise patterns
    float noise1 = turbulent(vec3(uv * 20.0 + iTime, iTime));
    float noise2 = turbulent(vec3(uv * 15.0 + iTime * 0.5, iTime));
    // Generate color from HSL with time-based shifts
    vec3 color = hsl(
        sin(iTime * 0.2 + uv.x * 4.0) * 0.5 + 0.5,
        1.0,
        0.6
    );
    // Add dynamic light source that moves over time
    vec2 lightPos = vec2(sin(iTime * 0.3) * 0.5 + 0.5, cos(iTime * 0.3) * 0.5 + 0.5);
    float lightDist = length(uv - lightPos);
    float lightIntensity = sin(lightDist * 10.0 + iTime * 2.0) * 0.5 + 0.5;
    // Combine noise patterns with color and lighting
    vec3 finalColor = mix(
        color,
        hsl(
            sin(iTime * 0.4 + uv.y * 4.0) * 0.5 + 0.5,
            1.0,
            0.8
        ),
        noise2 * 0.5 + 0.5
    ) * (1.0 - lightIntensity);
    // Add glow effect
    finalColor += vec3(
        noise1 * 0.4,
        noise2 * 0.6,
        (noise1 + noise2) * 0.2
    );
    return vec4(finalColor, 1.0);
}
