vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    float t = iTime * 0.5;
    vec2 rotated = uv * mat2(cos(t), sin(t), -sin(t), cos(t));

    // Scale and create noise patterns
    vec2 scaled = rotated * 5.0;
    float n1 = noise(scaled);
    float n2 = turbulent(vec3(scaled, t));

    // Create color variations using HSL
    vec3 hslColor = vec3(
        mod(t + n1 * 2.0, 1.0),
        0.7,
        0.5 + n2 * 0.4
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
