vec4 Effect2(vec2 uv) {
    float time = iTime * 0.1;

    // Generate particle field using noise
    vec3 layers = vec3(
        noise(uv * 5.0 + time),
        noise((uv + sin(time)) * 8.0),
        noise((uv - cos(time)) * 6.0)
    );

    float total = (layers.x + layers.y + layers.z) / 3.0;

    // Create color based on particle presence
    vec3 color = vec3(0.0);
    if(total > 0.5) {
        // Generate shifting colors using HSL
        float hue = fract(time * 0.2 + uv.x * 10.0) * 360.0;
        color = hsl2rgb(vec3(hue, 0.7, 0.8));
    }

    // Add glowing effect
    vec2 glowUV = uv;
    float glow = 0.0;
    for(float i = -1.5; i <= 1.5; i += 0.5) {
        for(float j = -1.5; j <= 1.5; j += 0.5) {
            vec2 offset = vec2(i, j) * 0.05;
            glow += noise(glowUV + offset);
        }
    }
    glow = (glow / 9.0) * 0.3;

    // Combine effects
    color *= smoothstep(0.0, 1.0, total);
    color += glow * vec3(1.0, 0.8, 0.6);

    return vec4(color, 1.0);
}
