vec4 Effect2(vec2 uv) {
    // Create time-based sine waves in both x and y directions
    float sinX = sin(uv.x * 10.0 + iTime);
    float cosY = cos(uv.y * 10.0 - iTime);

    // Combine the waves with noise
    vec2 noiseCoords = vec2(sinX, cosY) * 0.5 + 0.5;
    float noiseVal = noise(noiseCoords);

    // Add turbulence for more complex patterns
    float turb = turbulent(vec3(uv * 10.0, iTime));

    // Create a rotating color pattern using HSL
    vec3 hslColor = vec3(
        mod(iTime + uv.x * 2.0 + uv.y * 3.0, 1.0),  // Hue
        0.7 + noiseVal * 0.3,                         // Saturation
        0.5 + turb * 0.5                             // Lightness
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
