vec4 Effect2(vec2 uv) {
    // Center coordinates and add motion
    vec2 st = uv - 0.5;
    float radius = length(st);

    // Create rotating pattern using time
    float angle = atan2(st.y, st.x) + sin(iTime * 0.5) * 4.0;
    float r = radius * cos(angle);

    // Add noise and turbulence for organic feel
    vec3 noiseParams = vec3(r, iTime * 0.1, 0.0);
    float noiseValue = noise(noiseParams);

    // Generate color using HSL with time-based hue shift
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + r * 0.5),  // Hue varies with radius and time
        0.7,                            // Saturation
        0.5 + noiseValue * 0.2          // Lightness with noise variation
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine elements for final color
    return vec4(rgbColor * (1.0 - abs(r) * 0.5), 1.0);
}
