vec4 Effect2(vec2 uv) {
    // Create a grid pattern with animated sine waves
    float time = iTime * 0.5;

    // Base grid calculation
    vec2 st = uv * 10.0;
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Animate the grid with sine waves
    float wave = sin(ipos.x * 0.5 + time) * 0.5 + 0.5;
    wave += sin(ipos.y * 0.5 + time + PI*0.5) * 0.5;

    // Add noise and turbulence
    vec2 noisePos = ipos + vec2(time, time);
    float n1 = noise(noisePos);
    float n2 = turbulent(vec3(st, time));

    // Create color variations
    vec3 color1 = hsl(wave * 0.7 + 0.3, 0.8, 0.6);
    vec3 color2 = hsl((wave + n1) * 0.5 + 0.2, 0.7, 0.5);

    // Combine patterns
    float blend = smoothstep(0.0, 1.0, fpos.x + fpos.y - 0.5);
    vec3 finalColor = mix(color1, color2, blend);

    // Add distortion
    finalColor *= sin(time) * 0.5 + 0.7;
    finalColor += n2 * 0.3;

    return vec4(finalColor, 1.0);
}
