vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    vec2 rotated = uv * mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));

    // Create sine wave patterns with time offset
    float waveU = sin(rotated.x * 10.0 + iTime) * 0.5 + 0.5;
    float waveV = sin(rotated.y * 10.0 + iTime * 0.5) * 0.5 + 0.5;

    // Combine waves and add turbulence
    vec2 distortedUV = rotated + vec2(waveU, waveV) * 0.3;
    float turb = turbulent(distortedUV * 10.0);

    // Generate color shifts using noise
    vec3 colorNoise = vec3(noise(uv + vec2(iTime, 0.0)),
                          noise(uv + vec2(0.0, iTime*0.5)),
                          noise(uv + vec2(iTime*0.7, 0.0)));

    // Create dynamic HSL color
    vec3 hslColor = vec3(
        mod(turb * 0.5 + iTime * 0.1, 1.0),  // Hue
        0.8 + turb * 0.2,                     // Saturation
        0.5 + sin(iTime) * 0.4                // Lightness
    );

    // Convert to RGB and mix with noise
    vec3 finalColor = hsl2rgb(hslColor) * (1.0 + colorNoise * 0.5);

    return vec4(finalColor, 1.0);
}
