vec4 Effect2(vec2 uv) {
    // Create base pattern using UV coordinates and time
    vec2 st = uv * 5.0 + iTime;

    // Add warping effect using noise and turbulence
    st += noise(st + iTime * 0.5) * 0.3;
    st += turbulent(vec3(st, iTime)) * 0.1;

    // Create color variations using HSL
    vec3 color = hsl2rgb(vec3(
        mod(length(uv) * 10.0 + iTime * 2.0, 1.0),
        0.7,
        0.5
    ));

    // Add glow effect based on distance from center
    float glow = exp(-length(uv - vec2(0.5)) * 4.0);
    color *= glow;

    // Combine multiple layers for more complex pattern
    vec2 st2 = uv * 3.0 + iTime * 0.5;
    st2 += noise(st2) * 0.2;
    st2 += turbulent(vec3(st2, iTime)) * 0.1;

    // Add second color layer
    vec3 color2 = hsl2rgb(vec3(
        mod(length(uv - vec2(0.5)) * 8.0 + iTime * 3.0, 1.0),
        0.6,
        0.4
    ));

    // Combine both layers with some blending
    color = mix(color, color2, 0.3);

    return vec4(color, 1.0);
}
