vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for a denser pattern
    vec2 st = uv * 10.0;

    // Create a grid of points
    vec2 grid = floor(st);

    // Calculate distance to nearest node
    vec2 distToNode = fract(st);
    float minDist = min(distToNode.x, distToNode.y);
    minDist = min(minDist, 1.0 - distToNode.x);
    minDist = min(minDist, 1.0 - distToNode.y);

    // Create pulsing effect using time and noise
    float pulse = sin(iTime * 2.0 + noise(vec3(grid, iTime))) * 0.5 + 0.5;

    // Calculate glow intensity
    float glow = exp(-minDist * 10.0);

    // Create web lines by checking proximity to connecting points
    vec2 neighborDist = min(abs(fract(st) - 0.5), 1.0 - abs(fract(st) - 0.5));
    float lineIntensity = exp(-length(neighborDist) * 10.0);

    // Combine node glow and web lines
    float intensity = glow + lineIntensity;

    // Create pulsing color
    vec3 color = hsl2rgb(vec3(
        pulse * 0.6,   // Hue
        0.7,          // Saturation
        0.5 + pulse*0.5 // Lightness
    ));

    // Apply intensity to color
    color *= intensity;

    return vec4(color, 1.0);
}
