vec4 Effect2(vec2 uv) {
    // Create a flowing plasma effect with noise and RGB shifts

    // Animate based on time
    float t = iTime * 0.5;

    // Generate base color using noise patterns
    vec3 color = vec3(
        noise(uv + vec2(t, t*0.7)),
        noise(uv + vec2(t*0.6, t*1.2) + vec2(1.0)),
        noise(uv + vec2(t*0.8, t*0.9) + vec2(0.5))
    );

    // Add turbulence for more detail
    color += vec3(
        turbulent(uv * 4.0 + t),
        turbulent(uv * 6.0 - t),
        turbulent(uv * 8.0 + t*0.5)
    ) * 0.5;

    // Convert to HSL for better color control
    vec3 hslColor = hsl(
        mod(length(color) * 2.0 + t, 1.0), 
        0.7,
        0.6
    );

    // Shift RGB channels differently
    vec3 rgbShifted = hsv2rgb(vec3(
        hslColor.x + sin(t*0.5),
        hslColor.y * 0.8,
        hslColor.z * 1.2
    ));

    // Add flowing animation
    float flow = sin(uv.x * 4.0 + t) * cos(uv.y * 3.0 + t);
    rgbShifted += vec3(flow * 0.5, flow * 0.3, flow * 0.2);

    return vec4(rgbShifted, 1.0);
}
