vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 centered = uv - 0.5;
    float r = length(centered);
    float theta = atan2(centered.y, centered.x);

    // Add time-based rotation
    theta += 0.5 * iTime;

    // Create rotating sine wave patterns with dynamic RGB shifts
    vec3 color = vec3(
        sin(theta + r * 8.0 + iTime) * 0.5 + 0.5,
        sin(theta + r * 8.0 - iTime) * 0.5 + 0.5,
        sin(theta + r * 8.0 + 2.0*iTime) * 0.5 + 0.5
    );

    // Add some complexity to the pattern
    color += vec3(
        pow(sin(4.0*theta + iTime), 2.0) * 0.2,
        pow(sin(4.0*theta - iTime), 2.0) * 0.2,
        pow(sin(4.0*theta + 2.0*iTime), 2.0) * 0.2
    );

    // Add some noise for dynamic shifts
    float noiseVal = turbulent(uv * 8.0 + iTime);
    color += noiseVal * 0.3;

    return vec4(color, 1.0);
}
