vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Distance from center
    float d = length(st);

    // Polar coordinates
    vec2 polar = vec2(length(st), atan2(st.y, st.x));

    // Add time-based rotation
    polar.y += iTime * 0.5;

    // Create base pattern with noise
    float n1 = turbulent(polar * 4.0 + vec2(iTime)) * 0.5 + 0.5;
    float n2 = noise(vec3(polar * 8.0, iTime * 0.2));
    float n3 = turbulent(polar * 16.0) * 0.5 + 0.5;

    // Combine noise layers
    float pattern = (n1 + n2 * 0.7 + n3 * 0.4);

    // Create color transitions
    vec3 color1 = hsl2rgb(vec3(0.5 + pattern * 0.4, 0.8, 0.6));
    vec3 color2 = hsl2rgb(vec3(0.2 + pattern * 0.6, 0.7, 0.7));

    // Mix colors based on noise
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, n2 * 0.5 + 0.5));

    // Add pulsing effect
    float pulse = sin(iTime) * 0.2 + 0.8;
    finalColor *= (1.0 - d * 0.7) * pulse;

    // Add specular highlights
    vec3 lightDir = normalize(vec3(sin(iTime), cos(iTime), 1.0));
    float spec = pow(max(dot(normalize(vec3(st, 0.5)), lightDir), 0.0), 20.0);
    finalColor += vec3(1.0) * spec * 0.4;

    return vec4(finalColor, 1.0);
}
