vec4 Effect2(vec2 uv) {
    // Create base liquid metal color with dark blue/purple hues
    vec3 baseColor = hsl(240.0, 0.8, 0.1);

    // Add dynamic ripples using sine waves and noise
    float timeFactor = iTime * 0.5;
    float wave1 = sin(uv.x * 6.0 + timeFactor) * 0.5;
    float wave2 = sin(uv.y * 6.0 - timeFactor) * 0.5;
    float noiseValue = noise(vec3(uv * 4.0, iTime)) * 0.3;

    // Create turbulence effect for flowing movement
    vec2 turbulentUV = uv + vec2(turbulent(uv * 10.0 + timeFactor), turbulent(uv * 10.0 - timeFactor)) * 0.05;
    float flow = sin(length(turbulentUV) * 8.0 - iTime * 4.0) * 0.3;

    // Combine wave effects
    float ripple = (wave1 + wave2 + noiseValue + flow) * 0.5 + 0.5;

    // Add highlight/reflection effect
    vec2 reflectUV = uv - vec2(0.5, 0.5);
    float reflection = sin(length(reflectUV) * 8.0 - iTime * 4.0) * 0.3 + 0.7;
    vec3 highlightColor = hsl(60.0, 0.8, 0.5); // Bright yellow-orange

    // Combine all effects
    vec3 finalColor = mix(baseColor, highlightColor, reflection);
    finalColor *= ripple * 1.5; // Add intensity variation

    return vec4(finalColor, 1.0);
}
