vec4 Effect2(vec2 uv) {
    vec2 p = uv * 0.1 + vec2(sin(iTime * 0.5), cos(iTime * 0.3));
    vec2 q = uv * 0.1 + vec2(cos(iTime * 0.7), sin(iTime * 0.4));

    float noiseX = noise(p);
    float noiseY = noise(q);

    vec3 color = hsv2rgb(vec3(
        mod(noiseX * 5.0 + iTime * 0.1, 1.0),
        0.7 + noiseY * 0.3,
        0.8
    ));

    float t = turbulent(vec2(noiseX * 10.0, noiseY * 10.0));
    color *= vec3(0.5 + t * 0.5);

    return vec4(color, 1.0);
}
