vec4 Effect2(vec2 uv) {
    // Center and rotate UV coordinates over time
    vec2 st = uv - 0.5;
    float angle = iTime * 0.1;
    st = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    ) + 0.5;
    // Create a dynamic noise pattern with time
    vec3 pos = vec3(st, iTime * 0.1);
    float noiseVal = turbulent(pos * 4.0);
    // Calculate color components using HSL
    vec3 colorHSL;
    colorHSL.x = fract(noiseVal * 0.5 + iTime * 0.2); // Hue variation over time
    colorHSL.y = 0.7; // Saturation
    colorHSL.z = 0.5 + noiseVal * 0.3; // Lightness modulation
    // Add lighting effect using another noise layer
    float light = noise(st * 2.0 + iTime) * 0.4;
    colorHSL.z += light;
    // Convert HSL to RGB and return
    vec3 finalColor = hsl2rgb(colorHSL);
    return vec4(finalColor, 1.0);
}
