vec4 Effect2(vec2 uv) {
    // Create a base star field using noise
    float stars = noise(uv * 10.0);

    // Add pulsing animation with time
    float pulse = sin(iTime + stars * 5.0) * 0.5 + 0.5;

    // Combine noise and pulse for brightness variation
    float brightness = (stars + pulse) * 0.5;

    // Add turbulence for more dynamic movement
    brightness += turbulent(uv * 100.0) * 0.3;
    brightness += turbulent(uv * 200.0) * 0.2;

    // Create color variation using HSV to RGB conversion
    vec3 color = hsv2rgb(vec3(
        iTime * 0.5 + stars * 2.0,
        0.7 + brightness * 0.3,
        brightness
    ));

    return vec4(color, 1.0);
}
