vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 polar = uv - 0.5;
    float radius = length(polar);
    float angle = atan(polar.y, polar.x);

    // Create pulsing effect with time
    float pulse = sin(iTime * 0.5 + radius * 10.0) * 0.2 + 0.8;
    radius *= pulse;

    // Animate rotation speed
    angle += iTime * 3.0;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(angle / (2.0 * PI) + iTime * 0.1),  // Hue
        0.7 + sin(radius * 5.0 + iTime * 2.0) * 0.2,  // Saturation
        0.5 + cos(radius * 3.0 + iTime) * 0.4  // Lightness
    );

    // Convert to RGB and add noise for texture
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor += noise(vec3(uv * 10.0, iTime)) * 0.1;

    return vec4(rgbColor, 1.0);
}
