vec4 Effect2(vec2 uv) {
    vec4 result;

    // Base noise pattern with time variation
    float baseNoise = noise(vec3(uv * 10.0, iTime));

    // Add turbulence for fractal effect
    float turb = turbulent(vec3(uv * 20.0, iTime * 0.5));

    // Combine noise and turbulence with time-based color shifts
    vec3 color = hsl(
        0.5 + turb * 0.5 + iTime * 0.1,
        0.7 + noise(vec3(uv * 15.0, iTime)) * 0.2,
        0.5 + sin(iTime) * 0.4 + noise(vec3(uv * 25.0, iTime * 0.7)) * 0.2
    );

    result.rgb = hsl2rgb(color);
    return result;
}
