vec4 Effect2(vec2 uv) {
    // Create flowing liquid metal effect using noise and turbulence

    // Add time-based offset to create movement
    vec2 st = uv * 5.0 + vec2(iTime * 0.5, iTime * 0.3);

    // Use turbulent noise for organic patterns
    float noise1 = turbulent(st);
    float noise2 = turbulent(st * 2.0 + vec2(1.0));

    // Create color shifts using HSL
    vec3 colorBase = hsl2rgb(vec3(0.5 + noise1 * 0.4, 0.8, 0.6));
    vec3 colorShift = hsl2rgb(vec3(0.5 + noise2 * 0.4, 0.8, 0.6));

    // Combine colors with some metallic sheen
    vec3 finalColor = mix(colorBase, colorShift, sin(uv.x * 10.0 + iTime) * 0.5 + 0.5);

    // Add lighting effect
    vec3 lightDir = normalize(vec3(sin(iTime), cos(iTime), 1.0));
    float light = dot(normalize(vec3(noise(st), noise(st*2.0), 1.0)), lightDir) * 0.5 + 0.5;

    // Add edge detection for liquid effect
    float edge = length(uv);
    finalColor *= (1.0 - smoothstep(0.8, 1.0, edge)) * 0.7 + 0.3;

    return vec4(finalColor * light, 1.0);
}
