vec4 Effect2(vec2 uv) {
    // Create base sine wave pattern with varying frequency and phase
    float speed = 0.5;
    float freq1 = 3.0;
    float freq2 = 6.0;

    vec2 st = uv * 8.0; // Scale UV coordinates

    // Moving sine waves with different frequencies and phases
    float wave1 = sin(st.x * freq1 + iTime * speed) * 0.5 + 0.5;
    float wave2 = sin(st.y * freq2 + iTime * speed + PI/2.0) * 0.5 + 0.5;

    // Combine waves and add noise for complexity
    float value = (wave1 + wave2) * 0.5;
    value += turbulent(vec3(uv, iTime)) * 0.3; // Add depth with turbulence

    // Create color shifts based on depth
    vec3 color = hsl(value * 360.0, 0.7, 0.5);

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
