vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv - 0.5;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create rotating sine waves with increasing frequency
    float wave1 = sin(r * 4.0 + a * 3.0) * 0.5;
    float wave2 = sin(r * 8.0 - a * 2.0) * 0.3;

    // Add noise and turbulence for more organic feel
    vec2 noiseUV = uv * 10.0 + iTime;
    float noiseVal = noise(noiseUV);
    float turbulentVal = turbulent(vec2(r, a));

    // Combine waves with noise and turbulence
    float pattern = wave1 + wave2 + noiseVal * 0.3 + turbulentVal * 0.2;

    // Create color shifts using HSL to RGB conversion
    vec3 colorBase = hsl(a * 0.5 + iTime, 0.7, 0.6);
    vec3 colorVar = hsl(r * 0.5 + iTime * 0.3, 0.8, 0.7);

    // Combine colors based on pattern
    vec3 finalColor = mix(colorBase, colorVar, abs(pattern) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
