vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 5.0 + iTime;

    // Create multi-layered noise patterns
    float n1 = noise(st);
    float n2 = noise(st * 2.0 + iTime);
    float n3 = noise(st * 4.0 + iTime * 0.5);

    // Add turbulence for more dynamic movement
    float turb = turbulent(st + iTime) * 0.5;

    // Combine patterns with time-based variation
    float pattern = (n1 + n2 + n3 + turb) * 0.25;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(pattern + iTime * 0.1),   // Hue
        0.8,                            // Saturation
        0.5 + n3 * 0.4                  // Lightness
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
