vec4 Effect2(vec2 uv) {
    vec2 iTimeUV = sin(cos(uv * 10.0 + iTime * 5.0)) * 0.5;

    float noise1 = noise(uv * 50.0);
    float noise2 = noise((uv + iTime * 0.3) * 70.0);
    float noise3 = turbulent(vec2(noise1, noise2));

    vec3 rgb = hsl2rgb(hsv2rgb(vec3(
        (noise3 * 0.5 + 0.5) * 6.0,
        sin(iTime * 4.0 + noise3 * 8.0),
        cos(iTime * 2.0 + noise3 * 4.0)
    )));

    float bloom = smoothstep(0.0, 1.0, noise3);
    rgb *= vec3(bloom);

    return vec4(rgb, 1.0);
}
