vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 pos = uv * 5.0 + iTime;

    // Create flowing pattern using turbulence
    float t1 = turbulent(pos);
    float t2 = turbulent(pos + vec2(1.0, 1.0));

    // Combine turbulence values for color variation
    float colorMix = (t1 + t2 * 0.5) * 0.5;

    // Create base color using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.1 + pos.x * 0.5),
        0.7,
        0.5 + 0.5 * sin(iTime * 2.0 + pos.y)
    );

    // Convert to RGB and mix with turbulence
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 0.5 + 0.5 * colorMix;

    // Add flowing movement using noise
    float flow = noise(vec3(pos, iTime));
    flow = 0.5 + 0.5 * sin(flow * pi + iTime);

    // Combine all elements for final color
    vec3 finalColor = rgbColor * flow;

    return vec4(finalColor, 1.0);
}
