vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = (uv - 0.5) * 3.0 + iTime;

    // Create two perpendicular sine wave patterns with time offset
    float wave1 = sin(st.x * 5.0 + iTime * 2.0) * 0.5 + 0.5;
    float wave2 = sin(st.y * 5.0 + iTime * -2.0) * 0.5 + 0.5;

    // Add noise texture
    vec2 noiseScale = st * 4.0;
    float noise1 = turbulent(noiseScale);
    float noise2 = turbulent(noiseScale + 100.0);

    // Combine waves and noise
    float effect = (wave1 + wave2 + noise1 + noise2) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1 + st.x, 1.0),   // Hue variation over time and space
        0.7 + noise2 * 0.3,             // Saturation modulation
        0.5 + effect * 0.5              // Lightness based on wave pattern
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
