vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 uv_centered = uv - 0.5;
    float radius = length(uv_centered);
    float angle = atan2(uv_centered.y, uv_centered.x);
    // Add time-based rotation
    angle += sin(iTime * 0.5) + iTime;
    // Create interlocking gear-like pattern
    float segments = 16.0;
    float gear = abs(sin(angle * segments + iTime)) * 0.5 + 0.5;
    gear *= radius < 0.8 ? 1.0 : 0.0; // Keep within bounds
    // Add turbulence and noise
    vec3 noise_input = vec3(uv_centered.x, uv_centered.y, iTime);
    float turbulence = turbulent(noise_input * 2.0) * 0.5 + 0.5;

    // Combine patterns
    float pattern = gear * (1.0 - turbulence);
    // Color based on angle and time
    vec3 color = hsl2rgb(vec3(
        fract(angle / (2.0 * PI) + iTime * 0.2) * 360.0,
        0.7,
        0.5
    ));
    // Mix with background
    vec3 final_color = mix(vec3(0.1), color, pattern);
    return vec4(final_color, 1.0);
}
