vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create undulating ripples using sine waves with time-based modulation
    float distance = length(uv - 0.5);
    float wave1 = sin(distance * 8.0 + iTime * 4.0) * 0.5;
    float wave2 = sin(distance * 16.0 + iTime * 3.0) * 0.3;
    float wave3 = sin(distance * 32.0 + iTime * 2.0) * 0.2;

    // Combine waves for more complex pattern
    float ripples = (wave1 + wave2 + wave3) * 0.5 + 0.5;

    // Create color inversion effect based on proximity to mouse cursor
    vec2 mousePos = vec2(0.5); // Simulated mouse position for demonstration
    float proximity = length(uv - mousePos);
    vec3 invertedColor = 1.0 - color;

    // Mix colors based on proximity and ripples
    color = mix(color, invertedColor, smoothstep(0.0, 0.5, proximity));
    color *= ripples;

    // Add some turbulence to enhance the effect
    vec3 turbulence = vec3(turbulent(vec3(uv * 10.0, iTime)));
    color += turbulence * 0.2;

    return vec4(color, 1.0);
}
