vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = (uv - 0.5) * 12.0;

    // Add rotation based on time
    float angle = atan2(st.y, st.x) + iTime * 0.5;

    // Create rotating sine wave pattern
    float r = length(st);
    float d = sin(r * 4.0 + angle * 3.0) * cos(angle * 4.0);

    // Add color shifts using HSL
    vec3 color = hsl(
        mod(angle * 2.0 + iTime, 1.0),  // Hue
        0.7,                             // Saturation
        0.5 + d * 0.4                   // Lightness with dynamic shift
    );

    return vec4(hsl2rgb(color), 1.0);
}
