vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time-based rotation

    // Create concentric circles pattern with pulsing effect
    float circles = sin(r * 15.0 + iTime * 2.0) * 0.5 + 0.5;

    // Add turbulence for dynamic patterns
    vec3 noiseInput = vec3(st.x, st.y, iTime);
    float turb = turbulent(noiseInput * 0.5);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        a * 0.1 + iTime * 0.2, // Hue variation over time and angle
        0.7 + turb * 0.3,      // Saturation with turbulence
        0.5 + sin(iTime) * 0.5 // Lightness pulsing effect
    );

    // Convert HSL to RGB and mix with circles pattern
    vec3 color = hsl2rgb(hslColor);
    color *= circles;

    return vec4(color, 1.0);
}
