vec4 Effect2(vec2 uv) {
    // Create rotating polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add some noise and turbulence
    vec3 noiseInput = vec3(r * 10.0, theta * 10.0, iTime * 0.5);
    float t = turbulent(noiseInput);

    // Create color variations
    vec3 color1 = hsl(theta * 0.2 + 0.5, 0.7, 0.6);
    vec3 color2 = hsl(theta * 0.2 + 0.3, 0.8, 0.4);

    // Combine colors with turbulence
    vec3 finalColor = mix(color1, color2, t * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
