vec4 Effect2(vec2 uv) {
    // Create rotating coordinate system
    vec2 st = uv - 0.5;
    float angle = iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;
    // Polar coordinates for geometric patterns
    float r = length(rotated);
    float theta = atan2(rotated.y, rotated.x);
    // Create multiple shapes using sine waves with different frequencies
    float shape1 = sin(theta * 5.0 + iTime) * 0.5;
    float shape2 = sin(theta * 3.0 - iTime) * 0.7;
    float shape3 = sin(r * 10.0 + iTime) * 0.3;
    // Combine shapes and add turbulence
    float pattern = (shape1 + shape2 + shape3);
    pattern += turbulent(rotated * 5.0 + vec2(iTime)) * 0.2;

    // Dynamic color transitions using HSL
    vec3 hslColor = vec3(
        fract(iTime * 0.2 + theta / (2.0 * PI)),
        0.7,
        0.5 + pattern * 0.4
    );
    vec3 rgbColor = hsl2rgb(hslColor);
    // Add noise for extra detail
    float noiseValue = noise(rotated * 10.0 + vec2(iTime)) * 0.1;

    return vec4(rgbColor + noiseValue, 1.0);
}
