vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);
    // Create expanding circles using time and radius
    float circle = sin(r * 10.0 + iTime * 5.0);

    // Add noise variation to the circles
    vec3 noisePos = vec3(uv * 5.0, iTime * 0.5);
    float turbulence = turbulent(noisePos) * 0.5;

    // Combine circle pattern with noise
    float pattern = (circle + turbulence) * 0.5 + 0.5;

    // Create color cycling effect using HSL colors
    vec3 hslColor = vec3(
        theta / (2.0 * PI) + iTime * 0.1,  // Hue
        0.7,                                // Saturation
        0.5                                 // Lightness
    );

    // Convert HSL to RGB and mix with pattern
    vec3 rgbColor = hsl2rgb(hslColor);
    vec3 finalColor = rgbColor * pattern;

    return vec4(finalColor, 1.0);
}
