vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create base color using HSL
    vec3 color = hsl(
        mod(a + iTime * 0.5, 2.0 * PI) / (2.0 * PI),  // Hue based on angle and time
        0.7,  // Saturation
        0.5   // Lightness
    );
    // Add turbulence for dynamic effect
    vec3 noisePos = vec3(st.x + iTime, st.y + iTime, iTime * 0.1);
    float turb = turbulent(noisePos * 2.0) * 0.5 + 0.5;

    // Create motion blur effect using angle and time
    float motionBlur = sin(a + iTime) * 0.5 + 0.5;

    // Combine elements with radial gradient
    vec3 finalColor = mix(
        color,
        hsl2rgb(vec3(mod(iTime * 0.1, 1.0), 0.8, 0.6)),
        r * 0.5
    ) * (1.0 - turb) + turb;
    // Add motion blur effect
    finalColor *= mix(1.0, 0.3, motionBlur);
    return vec4(finalColor, 1.0);
}
