vec4 Effect2(vec2 uv) {
    // Create vertical stripe pattern using sine waves
    float stripes = sin(uv.x * 8.0 + iTime) * 0.5 + 0.5;
    stripes += sin(uv.x * 16.0 + iTime * 2.0) * 0.3;

    // Add noise to create organic variations
    vec2 noiseUV = uv * 4.0 + vec2(iTime);
    float noiseVal = noise(noiseUV);
    stripes += noiseVal * 0.15;

    // Create pulsing effect
    float pulse = sin(iTime * 0.5) * 0.3 + 0.7;

    // Generate color based on time and position
    vec3 color = hsl2rgb(vec3(
        fract(iTime * 0.1) * 360.0,   // Hue variation over time
        0.8,                          // Saturation
        stripes * pulse               // Brightness modulation
    ));

    return vec4(color, 1.0);
}
