vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 centered = uv * 2.0 - 1.0;
    float theta = atan2(centered.y, centered.x);
    float r = length(centered);

    // Create rotating effect by adding time to angle
    theta += iTime * 0.5;

    // Create color gradient using HSL colors
    vec3 color = hsl(theta / (pi * 2.0), 1.0, r);

    return vec4(hsl2rgb(color), 1.0);
}
