vec4 Effect2(vec2 uv) {
    vec2 pos = floor(uv * 10.0);
    float time = iTime * 0.5;

    // Calculate cell position and distance from center
    vec2 cellPos = pos + 0.5;
    vec2 centerOffset = uv - (pos + 0.5);
    float dist = length(centerOffset) * 10.0;

    // Pulsating color based on time and position
    float hue = fract(time * 0.1 + pos.x * 0.2 + pos.y * 0.3) * PI * 2.0;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Add noise and turbulence for more dynamic effect
    float noiseVal = noise(vec3(pos, time)) * 0.5 + 0.5;
    float pulse = sin(time + pos.x + pos.y) * 0.5 + 0.5;

    // Combine effects
    color *= hsl2rgb(vec3(hue, 1.0, noiseVal));
    color *= smoothstep(0.0, 1.0, sin(time + dist) * 0.5 + 0.5);

    // Final output with pulsating alpha
    return vec4(color, pulse);
}
