vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create a rotating pattern using time
    theta += sin(iTime * 0.5) * 0.5;

    // Generate fractal noise in polar coordinates
    vec3 noiseParams = vec3(r * 10.0, theta * 2.0 + iTime, 0.0);
    float turbulence = turbulent(noiseParams);

    // Create a radial gradient with color variations
    vec3 color = hsl2rgb(vec3(
        180.0 + sin(theta + iTime) * 60.0,
        0.7 + abs(turbulence) * 0.3,
        0.5 + sin(iTime * 0.2 + r * 4.0) * 0.5
    ));

    // Add pulsing effect
    float pulse = sin(iTime * 0.2) * 0.5 + 0.5;
    color *= (1.0 - r * 0.5) * (1.0 + pulse);

    // Mix in noise for more dynamic texture
    color += hsv2rgb(vec3(
        theta * 180.0,
        0.5,
        abs(turbulent(vec3(r * 5.0, theta * 2.0, 0.0))) * 0.4
    )) * (1.0 - r);

    // Add a soft glow effect
    color *= smoothstep(0.8, 1.0, 1.0 - r);

    return vec4(color, 1.0);
}
