vec4 Effect2(vec2 uv) {
    // Speed of movement
    float speed = 0.3;

    // Create a time-based offset for movement
    vec2 st = uv * 5.0 + vec2(sin(iTime * speed), cos(iTime * speed * 0.7));

    // Generate noise layers with different scales
    float n1 = noise(st * 10.0) * 0.5;
    float n2 = noise(st * 5.0 + vec2(1.0)) * 0.3;

    // Create a flowing movement pattern
    vec2 flow = vec2(
        sin(iTime * speed + st.x * 2.0),
        cos(iTime * speed * 0.7 + st.y * 2.0)
    );

    // Combine noise layers with turbulence for more complex patterns
    float turb = turbulent(st * 3.0) * 0.5;
    float pattern = (n1 + n2 + turb) * 0.5 + 0.5;

    // Create color shifts using HSL conversion
    vec3 color1 = hsl2rgb(vec3(
        sin(iTime * 0.2 + 1.0) * 0.5 + 0.5,
        0.7,
        0.8
    ));

    vec3 color2 = hsl2rgb(vec3(
        sin(iTime * 0.3 + 2.0) * 0.5 + 0.5,
        0.6,
        0.7
    ));

    // Mix colors based on pattern and flow
    vec3 finalColor = mix(color1, color2, pattern);
    finalColor *= sin(flow.x * pi + iTime) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
