vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time-based rotation

    // Create pulsating color effect using sine waves and time
    float hue = sin(a * 4.0 + iTime * 2.0) * 0.5 + 0.5;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Add geometric shape overlay with noise modulation
    float pattern = sin(r * 8.0 - iTime * 4.0) * 0.5 + 0.5;
    pattern += noise(vec3(uv * 10.0, iTime)) * 0.2;

    // Combine color and pattern with turbulence
    float turb = turbulent(vec2(a * 2.0, r * 4.0 - iTime));
    color *= pattern + turb * 0.5;

    return vec4(color, 1.0);
}
