vec4 Effect2(vec2 uv) {
    // Create time-varying waves
    float wave1 = sin(uv.x * 10.0 + iTime) * 0.5;
    float wave2 = sin(uv.y * 5.0 - iTime * 3.0) * 0.3;

    // Combine waves and add noise for texture
    float value = (wave1 + wave2) * 0.5 + noise(vec2(uv.x*5.0, uv.y*5.0)) * 0.3;

    // Create color transitions using HSL
    vec3 color = hsl(
        mod(iTime * 0.2 + value * 0.5, 1.0),  // Hue variation over time and space
        0.7,                                   // Saturation
        0.6                                    // Lightness
    );

    return vec4(color, 1.0);
}
