vec4 Effect2(vec2 uv) {
    // Base color with time-based shift
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.5*sin(iTime*0.1), 0.8, 0.3));

    // Create flowing noise patterns
    float noise1 = noise(uv * 4.0 + vec2(sin(iTime)*0.5, cos(iTime)*0.5)) * 0.5;
    float noise2 = noise(uv * 8.0 + vec2(cos(iTime*0.7)*0.3, sin(iTime*0.6)*0.3)) * 0.3;

    // Add turbulence for more dynamic movement
    vec2 distortedUV = uv + turbulent(uv * 4.0 + vec2(sin(iTime), cos(iTime))) * 0.1;

    // Create shimmering effect using highlight and color shift
    float highlight = sin(length(distortedUV) * 8.0 - iTime * 2.0) * 0.5 + 0.5;
    vec3 finalColor = mix(baseColor, hsl2rgb(vec3(0.1 + noise(uv*16.0), 0.9, 0.4)), highlight);

    return vec4(finalColor, 1.0);
}
