vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv + 0.5;
    float radius = length(st);
    float angle = atan2(st.y, st.x);
    // Create hexagonal grid pattern
    vec2 grid = vec2(
        floor(radius * 10.0),
        floor(angle / (pi/3.0) + 6.0)
    );
    // Calculate unique identifier for each hexagon
    float id = grid.x * 6.0 + grid.y;

    // Pulsing effect based on time and position
    float pulse = sin(iTime + id * 0.5);
    pulse = (pulse + 1.0) * 0.5; // Scale to 0-1 range

    // Calculate color based on hexagon position
    vec3 hslColor = vec3(
        angle / (2.0 * pi) * 360.0, // Hue based on angle
        0.7 + pulse * 0.3,         // Saturation increases with pulse
        0.5                       // Base lightness
    );

    // Convert to RGB and apply pulsing effect
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 0.8 + turbulent(uv * 10.0 + iTime) * 0.2; // Add noise

    return vec4(rgbColor, 1.0);
}
