vec4 Effect2(vec2 uv) {
    // Moving sine wave pattern with time offset
    float wave1 = sin(uv.x * 10.0 + iTime) * 0.5 + 0.5;
    float wave2 = sin(uv.y * 10.0 + iTime * 0.5 + pi) * 0.5 + 0.5;

    // Create a base color with time-varying hue
    vec3 baseColor = hsl2rgb(vec3(iTime * 0.1, 0.7, 0.6));

    // Add noise texture
    float noiseX = noise(uv * 40.0 + iTime) * 0.3;
    float noiseY = noise(uv * 40.0 + iTime + pi) * 0.3;

    // Combine waves and noise with color
    vec3 finalColor = baseColor * (wave1 + wave2 + noiseX + noiseY) * 1.5;

    return vec4(finalColor, 1.0);
}
